import style  from './SearchBar.module.scss'
import {Button, Form} from "react-bootstrap";
import {Autocomplete, TextField} from "@mui/material";
import {useState} from "react";
export  const SearchBar = (props) => {
    const ipAddress = '0';
    const Key = '9317d7bb66064a11a877fb466b8f452c';
    const [cities, setCities] = useState([]);

    const handelInputChange = (e)=>{
       const  value = e.currentTarget.value;
        const apiUrl = `https://api.geoapify.com/v1/geocode/search?text=${value}&apiKey=${Key}`;
        fetch(apiUrl)
            .then(response => response.json())
            .then(data => data && data.features.map((metadata) => {
             const   {city,country,lat, lon, formatted} =  metadata.properties;
             setCities([city,country,lat, lon, formatted])
            }));
    }
    return (
        <>
            <Form >
                <Form.Group className={`d-flex justify-content-center mt-5 gap-4` }>
                    <Form.Label>
                    <Autocomplete getOptionLabel={option => option.formatted} clearOnBlur={false} onInputChange={handelInputChange} className={style.searchInput} size={'lg'} renderInput={(params) =>  <TextField {...params} label={'Enter Your City '} /> } options={cities ?? ['casa', 'raba', 'laayoun', 'agadir']}/>
                    </Form.Label>
                    <Form.Label>
                    <Button size={'lg'}  variant={'primary'}>Search</Button>
                    </Form.Label>
                </Form.Group>
            </Form>
        </>
    );
};
