import {createSlice} from "@reduxjs/toolkit";

const initialState = {
    clouds : undefined,
    main : {
        temp : undefined
    },
    name : undefined,
    sys : undefined,
    timezone : undefined,
    weather : undefined,
    wind : undefined
}
 const WeatherSlice = createSlice({
    name: 'weather',
    initialState,
    reducers : {
        setData: (state, action) => {
            state = action.payload;
            console.log(state);
        }
    }
});

export const  {setData} = WeatherSlice.actions;
export default WeatherSlice.reducer;
